import React from "react";

class AddUser extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstname: "",
            lastname: "",
            bio: "",
            age: "",
            isHappy: false
        }
    }
    render() {
    return (
        <header>
            <h5>Создание нового пользователя</h5>
            <form>
            <div class="mb-3"><input placeholder="Имя" className="form-control"
            onChange={event => this.setState({userData: event.target.value})} /></div>
            <div class="mb-3"><input placeholder="Фамилия" className="form-control" /></div>
            <div class="mb-3"><textarea placeholder="Биография" className="form-control" /></div>
            <div class="mb-3"><input placeholder="Возраст" className="form-control" /></div>
                    <div class="mb-3 form-check">
                <label htmlFor="isHappy" className="form-check-label">Погода</label>
                <input type="checkbox" id="isHappy" className="form-check-input" /><br />
                    </div>
            <div class="mb-3"><button type="button" className="btn btn-success">Создать пользователя</button></div>
            </form>
        </header>
    )
  }
  }

  export default AddUser
