import React from "react";

class User extends React.Component {
    user = this.props.user    
    render() {
    return (
        <div className="bg-users">
        <h5>
        <i className="icon fa-user"></i> {this.user.id}. {this.user.firstname} {this.user.lastname} (возраст {this.user.age} лет)</h5>
                        {this.user.bio}<br />
            <b>Погода: {this.user.isHappy ? "все гуд" : "все плохо"}</b>
        </div>
    )
  }
  }

  export default User
