import React from "react";
import * as ReactDOMClient from "react-dom/client";

class Header extends React.Component {
    render() {
    return (
        <p>Даттебайо</p>
    )
}
}

let i = 0;

class App extends React.Component {

    inputClick() {
        console.log("Нажато " + i + " раз")
        i++;
    }

    onInput(e) {
        console.log(e.nativeEvent.data)
        let inp = document.getElementById('inp1');
        inp.innerHTML = e.nativeEvent.data;
    }

    render() {
        return (<div>
            <Header />
            <input id="inputForm" placeholder="Введите логин" 
            onInput = {this.onInput} onClick = {this.inputClick} />
            <textarea id="inp1"></textarea>
            <Header />
            </div>
            )
    }
}

const app = ReactDOMClient.createRoot(document.getElementById("app"));

app.render(<App />);
