import React from "react";


let i = 0;

class Input1 extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      justtext: "",
      userData: ""
    }
    this.inputClick = this.inputClick.bind(this)
  } 
  render() {
  return (
    <div>
    <p>{this.state.justtext}</p>
    <div class="col-md-4 input-group mb-3">
    <span class="input-group-text" id="basic-addon3">Введите сообщение</span>
    <input type="text" class="form-control" id="basic-url inputForm"
    onChange={event => this.setState({userData: event.target.value})}
    onInput = {this.onInput} onFocus = {this.inputClick} aria-describedby="basic-addon3" />
    </div>
    <p><mark>{this.state.userData}</mark></p>
    </div>
  )
}

  inputClick() {
    this.setState({justtext: "Henge no Jutsu!"}) 
    console.log("Нажато " + i + " раз")
    i++;
  }

  onInput(e) {
    const inp = document.getElementById('inp1');
    inp.innerHTML = e.nativeEvent.data;
  }

}

export default Input1
