import React from "react"
import ReactDOM from "react-dom"

let i = 0;
function inputClick() {
    console.log("Нажато " + i)
    i++;
    let inp = document.getElementById('input');
}

function onInput() {
    console.log("Введено что-то")
    let inp = document.getElementById('input');
    inp.innerHTML = "Привет";
}


const app = document.getElementById("root");
const inp1 = <input idName="inputForm" placeholder="Введите логин" onInput = {onInput} onClick = {inputClick} />;
const btn = <p idName="input">Привет</p>;


ReactDOM.render(<div>{inp1} {btn}</div>, app);
