import React from "react";
import Image from './img'

class Header extends React.Component {
  render() {
  return (
      <header>{this.props.title}</header>
  )
}
}

let i = 0;

class App extends React.Component {

  inputClick() {
      console.log("Нажато " + i + " раз")
      i++;
  }

  onInput(e) {
      let inp = document.getElementById('inp1');
      inp.innerHTML = e.nativeEvent.data
      console.log(e.nativeEvent.data)
  }

  render() {
      return (<div>
          <Header title="Это React" />
          <input id="inputForm" placeholder="Введите логин" 
          onInput = {this.onInput} onClick = {this.inputClick} /><br /><br />
          <textarea id="inp1"></textarea>
          <Header />
          <Image image={Image}/>
          </div>
          )
  }
}

export default App
