import React from "react";
import Button from 'react-bootstrap/Button';

class User extends React.Component {
    user = this.props.user    
    render() {
    return (
        <div className="bg-users">
        <h5>
        <i className="bi bi-person-circle"></i> {this.user.id}. {this.user.firstname} {this.user.lastname} (возраст {this.user.age})</h5>
                        {this.user.bio}<br />
            <b>Погода: {this.user.isHappy ? "Тепло" : "Холодно"}</b><br /><br />
        <p>
            <button type="button" className="btn btn-outline-danger"><i className="bi bi-x-circle"></i></button>&nbsp;
            <button type="button" className="btn btn-outline-danger"><i className="bi bi-pencil-square"></i></button>
            <div className="mb-2">
        <Button variant="primary" size="lg">
          Large button
        </Button>{' '}
        <Button variant="secondary" size="lg">
          Large button
        </Button>
      </div>
        </p>
        </div>
    )
  }
  }

  export default User
