import React from "react";
import Image from "./Image"
import logo from './img/logo.png'

class Header extends React.Component {
  render() {
  return (
      <header>{this.props.title}</header>
  )
}
}

let i = 0;

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      justtext: "Изменение текста",
      userData: ""
    }
    this.inputClick = this.inputClick.bind(this)
    this.onInput = this.onInput.bind(this)
  }
  justtext = "Изменение текста"
  render() {
      return (<div>
          <Header title="Это React" />
          <p>{this.state.justtext}</p>
          <input id="inputForm" placeholder="Введите текст" 
          onInput = {this.onInput} onClick = {this.inputClick} /><br /><br />
          <textarea id="inp1"></textarea>
          <Image image={logo} alt="" />
          </div>
          )
  }
  inputClick() {
    this.setState({justtext: "Henge no Jutsu!"}) 
    console.log("Нажато " + i + " раз")
    i++;
}

  onInput(e) {
    const inp = document.getElementById('inp1');
    this.setState({inp: e.nativeEvent.data })
}
}

export default App
