import React from "react";
import TopHeader from './Header'
import Users from './Users'

let i = 0;

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      justtext: "Изменение текста",
      userData: ""
    }
    if (userData = '')
        userData == 0;
    this.inputClick = this.inputClick.bind(this)
  }
  justtext = "Изменение текста"
  render() {
      return (<div>
          <TopHeader title="Разработка программы на React + Bootstrap" />
          <br />
          <p>{this.state.justtext}</p>
          <div class="col-md-4 input-group mb-3">
          <span class="input-group-text" id="basic-addon3">Введите сообщение для вывода ниже</span>
          <input type="text" class="form-control" id="basic-url inputForm"
          onChange={event => this.setState({userData: event.target.value})}
          onInput = {this.onInput} onClick = {this.inputClick} aria-describedby="basic-addon3" />
          </div>
          <p><mark>{this.state.userData}</mark></p>
          <main>
          <Users />
          </main>
          <aside>

          </aside>


          </div>
          )
  }
  inputClick() {
    this.setState({justtext: "Henge no Jutsu!"}) 
    console.log("Нажато " + i + " раз")
    i++;
}

  onInput(e) {
    const inp = document.getElementById('inp1');
    inp.innerHTML = e.nativeEvent.data;
}
}
export default App
