import React from "react";
import TopHeader from './Header'
import CardHeader from './Bootstrap'
import Users from './Users'

let i = 0;

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      justtext: "Изменение текста",
      userData: ""
    }
    this.inputClick = this.inputClick.bind(this)
  }
  justtext = "Изменение текста"
  render() {
      return (<div>
          <TopHeader title="Разработка на React + Bootstrap" />
          <br />
          <p>{this.state.justtext}</p>
          <input className="form-control" id="inputForm" placeholder="Введите текст" onChange={event => this.setState({userData: event.target.value})}
          onInput = {this.onInput} onClick = {this.inputClick} /><br />
          <p>{this.state.userData}</p>
          <main>
          <Users />
          </main>
          <aside>
          <Card />
          </aside>


          </div>
          )
  }
  inputClick() {
    this.setState({justtext: "Henge no Jutsu!"}) 
    console.log("Нажато " + i + " раз")
    i++;
}

  onInput(e) {
    const inp = document.getElementById('inp1');
    inp.innerHTML = e.nativeEvent.data;
}
}
export default App
