import React from "react";
import Header from './Header'
import Boot from './Bootstrap'

let i = 0;

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      justtext: "Изменение текста",
      userData: ""
    }
    this.inputClick = this.inputClick.bind(this)
  }
  justtext = "Изменение текста"
  render() {
      return (<div>
          <Header title="Это React" />
          <p>{this.state.justtext}</p>
          <input id="inputForm" placeholder="Введите текст" onChange={event => this.setState({userData: event.target.value})}
          onInput = {this.onInput} onClick = {this.inputClick} /><br /><br />
          <p>{this.state.userData}</p>

          <Boot />

          </div>
          )
  }
  inputClick() {
    this.setState({justtext: "Henge no Jutsu!"}) 
    console.log("Нажато " + i + " раз")
    i++;
}

  onInput(e) {
    const inp = document.getElementById('inp1');
    inp.innerHTML = e.nativeEvent.data;
}
}
export default App
