import React from "react";

class Users extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      users: [
          {
            id: 1,
            firstname: "Vadim",
            lastname: "Khakimyanov",
            bio: 'Программист получаеца',
            age: 27,
            isHappy: true
        },
        {
            id: 2,
            firstname: "Ruslan",
            lastname: "Melnikov",
            bio: 'Не-промоутер получаеца',
            age: 18,
            isHappy: true
      },
      {
        id: 3,
        firstname: "Naruto",
        lastname: "Uzumaki",
        bio: 'Хокаге получаеца',
        age: 35,
        isHappy: false
      },
    ]   
  }
}

    render() {
      if (this.state.users.length > 0)
        return (
          <>
            {this.state.users.map((e) => (
            <div key={e.id}>
                <div className="bg-users">
                    <h5>{e.id}. {e.firstname} {e.lastname} (возраст {e.age} лет)</h5>
                    {e.bio}<br />
                    <b>Погода: {e.isHappy ? "все гуд" : "все плохо"}</b>
                    </div>
            </div>))}
        </>)
    else 
      return (
            <div className="bg-users">
                    <h3>Все уехали</h3>
            </div>
            )
    }
}

export default Users
