import React from "react";
import Button from 'react-bootstrap/Button';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';

class User extends React.Component {
    user = this.props.user    
    render() {
    return (
        <div className="bg-users">
        <h5>
        <i className="bi bi-person-circle"></i> {this.user.id}. {this.user.firstname} {this.user.lastname} (возраст {this.user.age})</h5>
                        {this.user.bio}<br />
            <b>Погода: {this.user.isHappy ? "Тепло" : "Холодно"}</b><br /><br />
        <Button variant="danger" size="sm"><i className="bi bi-x-circle"></i></Button>&nbsp;
        <Button variant="danger" size="sm"><i className="bi bi-pencil-square"></i></Button>

        {['top', 'right', 'bottom', 'left'].map((placement) => (
          <OverlayTrigger
            key={placement}
            placement={placement}
            overlay={
              <Tooltip id={`tooltip-${placement}`}>
                Tooltip on <strong>{placement}</strong>.
              </Tooltip>
            }
          >
            <Button variant="secondary">Tooltip on {placement}</Button>
          </OverlayTrigger>
        ))}

        </div>
    )
  }


  }

  export default User
